BEGIN TRAN
BEGIN TRY
DECLARE @ErrorMessage NVARCHAR(4000)
		DECLARE @ErrorSeverity INT
		DECLARE @ErrorState INT
		
EXEC [dbo].[versionСheck] 544

--DECLARE @NsiAct-------------------------------------------------------------------------------------------
	declare @NsiAct table 
			(
				IDHVID nvarchar(12),HVIDNAME nvarchar(1000),DATEBEG date,DATEEND date
			)

--INSERT NEW INTO  NsiCur------------------------------------------------------------------------------------------------------------
	select 
	NsiAct.IDHVID,NsiAct.HVIDNAME,NsiAct.DATEBEG,NsiAct.DATEEND, 
	REPLACE(NsiAct.IDHVID, '.', '')
	from @NsiAct NsiAct
		left join V018 NSiCur
			on NSiCur.IDHVID=NsiAct.IDHVID
	where 
		NSiCur.IDHVID is null

--UPDATE NsiCur------------------------------------------------------------------------------------------------------------
	update NsiCur set
NsiCur.DATEBEG=NsiAct.DATEBEG,
NsiCur.DATEEND=NsiAct.DATEEND,
NsiCur.IDHVID=NsiAct.IDHVID,
NsiCur.HVIDNAME=NsiAct.HVIDNAME
	from @NsiAct NsiAct
		inner join V018 NsiCur
						on NSiCur.IDHVID=NsiAct.IDHVID
	where
		isnull(NsiCur.DATEBEG,'19000101')<>isnull(NsiAct.DATEBEG,'19000101') or
isnull(NsiCur.DATEEND,'19000101')<>isnull(NsiAct.DATEEND,'19000101') or
isnull(NsiCur.IDHVID,'')<>isnull(NsiAct.IDHVID,'') or
isnull(NsiCur.HVIDNAME,'')<>isnull(NsiAct.HVIDNAME,'') 

EXEC [dbo].[versionUpdate] 545
COMMIT TRAN;		
END TRY
		BEGIN CATCH			
			SET @ErrorMessage= ERROR_MESSAGE()
			SET @ErrorSeverity= ERROR_SEVERITY()
			SET @ErrorState= ERROR_STATE()
			RAISERROR (@ErrorMessage,@ErrorSeverity,@ErrorState)
			IF (@@trancount > 0) ROLLBACK TRAN						
		END CATCH
IF (@@trancount > 0) ROLLBACK TRAN;