BEGIN TRAN
BEGIN TRY
DECLARE @ErrorMessage NVARCHAR(4000)
		DECLARE @ErrorSeverity INT
		DECLARE @ErrorState INT
		
EXEC [dbo].[versionСheck] 546

--DECLARE @NsiAct-------------------------------------------------------------------------------------------
	declare @NsiAct table 
			(
				IDHM numeric(3,0),HMNAME nvarchar(1000),DIAG nvarchar(1000),HVID nvarchar(12),HGR numeric(3,0),HMODP nvarchar(1000),IDMODP numeric(5,0),DATEBEG date,DATEEND date
			)

--INSERT NEW INTO  NsiCur------------------------------------------------------------------------------------------------------------
		--,IDMODP
		,DATEBEG,DATEEND,id)
	select 
		NsiAct.IDHM,NsiAct.HMNAME,NsiAct.DIAG,NsiAct.HVID,NsiAct.HGR 
		--,NsiAct.HMODP
		,NsiAct.IDMODP,NsiAct.DATEBEG,NsiAct.DATEEND,NsiAct.IDHM
	from @NsiAct NsiAct
		left join V019 NSiCur
			on NSiCur.IDHM=NsiAct.IDHM
	where 
		NSiCur.IDHM is null

--UPDATE NsiCur------------------------------------------------------------------------------------------------------------
	update NsiCur set
NsiCur.DATEBEG=NsiAct.DATEBEG,
NsiCur.DATEEND=NsiAct.DATEEND,
NsiCur.IDHM=NsiAct.IDHM,
NsiCur.HGR=NsiAct.HGR,
--NsiCur.IDMODP=NsiAct.IDMODP,
NsiCur.HMNAME=NsiAct.HMNAME,
NsiCur.DIAG=NsiAct.DIAG,
NsiCur.HVID=NsiAct.HVID,
NsiCur.HMODP=NsiAct.HMODP
	from @NsiAct NsiAct
		inner join V019 NsiCur
						on NSiCur.IDHM=NsiAct.IDHM
	where
		isnull(NsiCur.DATEBEG,'19000101')<>isnull(NsiAct.DATEBEG,'19000101') or
isnull(NsiCur.DATEEND,'19000101')<>isnull(NsiAct.DATEEND,'19000101') or
isnull(NsiCur.IDHM,'0')<>isnull(NsiAct.IDHM,'0') or
isnull(NsiCur.HGR,'0')<>isnull(NsiAct.HGR,'0') or
--isnull(NsiCur.IDMODP,'0')<>isnull(NsiAct.IDMODP,'0') or
isnull(NsiCur.HMNAME,'')<>isnull(NsiAct.HMNAME,'') or
isnull(NsiCur.DIAG,'')<>isnull(NsiAct.DIAG,'') or
isnull(NsiCur.HVID,'')<>isnull(NsiAct.HVID,'') or
isnull(NsiCur.HMODP,'')<>isnull(NsiAct.HMODP,'') 

EXEC [dbo].[versionUpdate] 547
COMMIT TRAN;		
END TRY
		BEGIN CATCH			
			SET @ErrorMessage= ERROR_MESSAGE()
			SET @ErrorSeverity= ERROR_SEVERITY()
			SET @ErrorState= ERROR_STATE()
			RAISERROR (@ErrorMessage,@ErrorSeverity,@ErrorState)
			IF (@@trancount > 0) ROLLBACK TRAN						
		END CATCH
IF (@@trancount > 0) ROLLBACK TRAN;