BEGIN TRAN
BEGIN TRY
DECLARE @ErrorMessage NVARCHAR(4000)
		DECLARE @ErrorSeverity INT
		DECLARE @ErrorState INT
		
EXEC [dbo].[versionСheck] 596


--DECLARE @NsiAct-------------------------------------------------------------------------------------------
	declare @NsiAct table 
			(
				IDUMP numeric(1,0),K_KSG nvarchar(20),N_KSG nvarchar(254),KOEF_Z numeric(4,2),DATEBEG date,DATEEND date
			)

--INSERT NEW INTO  NsiCur------------------------------------------------------------------------------------------------------------
	select 
	NsiAct.IDUMP,NsiAct.K_KSG,NsiAct.N_KSG,NsiAct.KOEF_Z,NsiAct.DATEBEG,NsiAct.DATEEND
	from @NsiAct NsiAct
		left join V023 NSiCur
			on NSiCur.DATEBEG=NsiAct.DATEBEG
 and NSiCur.IDUMP=NsiAct.IDUMP
 and NSiCur.K_KSG=NsiAct.K_KSG

	where 
		NSiCur.K_KSG is null

--UPDATE NsiCur------------------------------------------------------------------------------------------------------------
	update NsiCur set
NsiCur.DATEBEG=NsiAct.DATEBEG,
NsiCur.DATEEND=NsiAct.DATEEND,
NsiCur.IDUMP=NsiAct.IDUMP,
NsiCur.KOEF_Z=NsiAct.KOEF_Z,
NsiCur.K_KSG=NsiAct.K_KSG,
NsiCur.N_KSG=NsiAct.N_KSG
	from @NsiAct NsiAct
		inner join V023 NsiCur
						on NSiCur.DATEBEG=NsiAct.DATEBEG
 and NSiCur.IDUMP=NsiAct.IDUMP
 and NSiCur.K_KSG=NsiAct.K_KSG

	where
		isnull(NsiCur.DATEBEG,'19000101')<>isnull(NsiAct.DATEBEG,'19000101') or
isnull(NsiCur.DATEEND,'19000101')<>isnull(NsiAct.DATEEND,'19000101') or
isnull(NsiCur.IDUMP,'0')<>isnull(NsiAct.IDUMP,'0') or
isnull(NsiCur.KOEF_Z,'0')<>isnull(NsiAct.KOEF_Z,'0') or
isnull(NsiCur.K_KSG,'')<>isnull(NsiAct.K_KSG,'') or
isnull(NsiCur.N_KSG,'')<>isnull(NsiAct.N_KSG,'') 



EXEC [dbo].[versionUpdate] 597
COMMIT TRAN;		
END TRY
		BEGIN CATCH			
			SET @ErrorMessage= ERROR_MESSAGE()
			SET @ErrorSeverity= ERROR_SEVERITY()
			SET @ErrorState= ERROR_STATE()
			RAISERROR (@ErrorMessage,@ErrorSeverity,@ErrorState)
			IF (@@trancount > 0) ROLLBACK TRAN						
		END CATCH
IF (@@trancount > 0) ROLLBACK TRAN;