--INSERT NEW INTO  NsiCur------------------------------------------------------------------------------------------------------------
	select 
	NsiAct.Kod, NsiAct.Kod,ISNULL(NsiAct.IDVID, 0),NsiAct.Naim,NsiAct.Osn,NsiAct.Komment,NsiAct.KodPG,NsiAct.DATEBEG,NsiAct.DATEEND
	from @NsiAct NsiAct
		left join F014 NSiCur
			on NSiCur.DATEBEG=NsiAct.DATEBEG
 and NSiCur.Osn=NsiAct.Osn
	WHERE NSiCur.Osn is null AND NsiAct.kod NOT IN (SELECT id FROM F014)
	AND NsiAct.DATEEND IS NULL

--UPDATE NsiCur------------------------------------------------------------------------------------------------------------
	update NsiCur set
NsiCur.DATEBEG=NsiAct.DATEBEG,
NsiCur.DATEEND=NsiAct.DATEEND,
NsiCur.Kod=NsiAct.Kod,
NsiCur.IDVID=isnull(NsiAct.IDVID, 0),
NsiCur.Naim=NsiAct.Naim,
NsiCur.Osn=NsiAct.Osn,
NsiCur.Comments=NsiAct.Komment,
NsiCur.KodPG=NsiAct.KodPG
	from @NsiAct NsiAct
		inner join F014 NsiCur
						on NSiCur.DATEBEG=NsiAct.DATEBEG
 and NSiCur.Osn=NsiAct.Osn

	where
		isnull(NsiCur.DATEBEG,'19000101')<>isnull(NsiAct.DATEBEG,'19000101') or
isnull(NsiCur.DATEEND,'19000101')<>isnull(NsiAct.DATEEND,'19000101') or
isnull(NsiCur.Kod,'0')<>isnull(NsiAct.Kod,'0') or
isnull(NsiCur.IDVID,'0')<>isnull(NsiAct.IDVID,'0') or
isnull(NsiCur.Naim,'')<>isnull(NsiAct.Naim,'') or
