--INSERT NEW INTO  NsiCur------------------------------------------------------------------------------------------------------------
	select 
	NsiAct.IDVID, NsiAct.IDVID,NsiAct.VIDNAME,NsiAct.DATEBEG,NsiAct.DATEEND
	from @NsiAct NsiAct
		left join F006 NSiCur
			on NSiCur.DATEBEG=NsiAct.DATEBEG
